
public class Remainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 5;
		int b = 8;
		int c = 4;
		
		System.out.println((a + b) % c);
		System.out.println((a % c) + (b % c) % c);
		System.out.println((a * b) % c);
		System.out.println((a % c) * (b % c) % c);
		
	}

}
